'use strict';
/* eslint no-console: 0 */
// Do this as the first thing so that any code reading it knows the right env.

process.env.NODE_ENV = 'production';

// Makes the script crash on unhandled rejections instead of silently
// ignoring them. In the future, promise rejections that are not handled will
// terminate the Node.js process with a non-zero exit code.
process.on('unhandledRejection', function (err) {
  throw err;
});

var colors = require('chalk');
var rimraf = require('rimraf');

var createWebpackCompiler = require('./utils/create-webpack-compiler');
var paths = require('./config/paths');
var webpackConfigProd = require('./config/webpack.config.prod');

function build(config) {
  var compiler = createWebpackCompiler(config);

  compiler.run(function (err) {
    if (err) {
      throw err;
    }

    console.log('');
    console.log(colors.green('Compiled successfully.'));
  });
}

rimraf(paths.appBuild, function (err) {
  if (err) {
    throw err;
  }
  build(webpackConfigProd);
});