'use strict';

var webpack = require('webpack');

var paths = require('./paths');
var webpackConfigBase = require('./webpack.config.base');

var webpackConfigProd = Object.assign({}, webpackConfigBase);

webpackConfigProd.entry = {
  'index.bundle.min': [paths.appIndexJs]
};

webpackConfigProd.output.pathinfo = false;
webpackConfigProd.target = 'node';

webpackConfigProd.plugins.push(new webpack.optimize.UglifyJsPlugin({
  include: /\.min\.js$/,
  minimize: true,
  compress: {
    warnings: false
  }
}));

module.exports = webpackConfigProd;