'use strict';
/* eslint no-console: 0 */

process.env.NODE_ENV = 'development';

process.on('unhandledRejection', function (err) {
  throw err;
});

var colors = require('chalk');
var WebpackDevServer = require('webpack-dev-server');

var createWebpackCompiler = require('./utils/create-webpack-compiler');
var webpackConfigDev = require('./config/webpack.config.dev');
var webpackDevServerConfig = require('./config/webpackDevServer.config');
var options = require('./utils/parse-options');

/**
 * run webpack dev server
 * @param  {Number} port server port
 */
function start(port) {
  var compiler = createWebpackCompiler(webpackConfigDev);

  var server = new WebpackDevServer(compiler, webpackDevServerConfig);

  server.listen(port, function (err) {
    if (err) {
      console.log(colors.red('[ERR]: Failed to webpack dev server'));
      console.error(err.message || err);
      process.exit(1);
    }

    var serverUrl = options.protocol + '//' + options.host + ':' + options.port + '/';
    console.log('');
    console.log('');
    console.log(colors.green('Starting the development server at:'));
    console.log('    ' + colors.underline.white(serverUrl));
    console.log('');
  });
}

start(options.port);