'use strict';

var address = require('address');

/**
 * Parst option with process.env and process.args
 *
 * @return {Object}
 */
var protocol = process.env.HTTPS === 'true' ? 'https:' : 'http:';
var host = process.env.HOST || address.ip();
var port = parseInt(process.env.PORT, 10) || 9999;

module.exports = {
  host: host,
  protocol: protocol,
  port: port
};